import React, { useEffect, useState } from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";
import { useNavigate, useParams } from "react-router-dom";
import { getUserById, updateUser } from "../Service/UserService"; // Import các hàm từ service
import { storage } from "../FirebaseConfig/firebase";
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";

export default function UpdateUserForm() {
    const [user, setUser] = useState(null);
    const [url, setUrl] = useState("");
    const navigate = useNavigate();
    const { userId } = useParams(); // Lấy userId từ URL
    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6ImNoYW5odHYiLCJzdWIiOiJjaGFuaHR2IiwiZXhwIjoxNzMzNDU0MTYwfQ.kj4qPTp9b9WLx4NeIrEGuwWakr7QR1Td7Zqu3LkIGR4'; // Thay thế bằng token của bạn

    // Hàm lấy dữ liệu người dùng
    useEffect(() => {
        const fetchUser = async () => {
            const fetchedUser = await getUserById(userId, token);
            console.log('Fetched user:', fetchedUser); // In ra toàn bộ user
            setUser(fetchedUser);
            setUrl(fetchedUser.imgUrl); // Cập nhật URL ảnh
        };

        fetchUser();
    }, [userId, token]);

    // Kiểm tra nếu người dùng chưa được lấy
    if (!user) return <div>Loading...</div>;

    const initialValues = {
        username: user.username,
        fullName: user.fullName,
        address: user.address,
        numberphone: user.numberphone,
        gender: user.gender,
        birthday: user.birthday.split('T')[0], // Định dạng lại ngày sinh
        salary: user.salary,
        roleId: user.roleId,
        email: user.email,
        imgUrl: user.imgUrl,
    };

    const validateUser = {
        fullName: Yup.string().required("Tên là bắt buộc."),
        address: Yup.string().required("Địa chỉ là bắt buộc."),
        numberphone: Yup.string().required("Số điện thoại là bắt buộc."),
        username: Yup.string().required("Tên đăng nhập là bắt buộc."),
        email: Yup.string().email("Email không hợp lệ").required("Email là bắt buộc."),
        salary: Yup.number().min(0, "Lương phải lớn hơn 0.").required("Lương là bắt buộc."),
        roleId: Yup.string().required("Quyền là bắt buộc."),
    };

    const handleUpload = async (file, setFieldValue) => {
        const storageRef = ref(storage, `images/${file.name}`);
        try {
            const snapshot = await uploadBytes(storageRef, file);
            const downloadURL = await getDownloadURL(snapshot.ref);
            setFieldValue("imgUrl", downloadURL);
            setUrl(downloadURL);
        } catch (error) {
            console.error("Upload failed:", error);
        }
    };

    const handleImageChange = (e, setFieldValue) => {
        const file = e.target.files[0];
        if (file) {
            handleUpload(file, setFieldValue);
        }
    };

    const handleSubmitUser = async (values) => {
        try {
            await updateUser(userId, values, token); // Gọi hàm cập nhật người dùng
            navigate("/users");
        } catch (error) {
            console.error("Có lỗi xảy ra khi cập nhật người dùng.", error);
        }
    };

    return (
        <div className="update-user-container">
            <Formik
                initialValues={initialValues}
                onSubmit={handleSubmitUser}
                validationSchema={Yup.object(validateUser)}
            >
                {({ setFieldValue }) => (
                    <Form>
                        <h1>Cập nhật Người Dùng</h1>

                        <div>
                            <label>Tên Đầy Đủ:</label>
                            <Field name="fullName" />
                            <ErrorMessage name="fullName" component="span" />
                        </div>

                        <div>
                            <label>Địa Chỉ:</label>
                            <Field name="address" />
                            <ErrorMessage name="address" component="span" />
                        </div>

                        <div>
                            <label>Ngày Sinh:</label>
                            <Field name="birthday" type="date" />
                            <ErrorMessage name="birthday" component="span" />
                        </div>

                        <div>
                            <label>Số Điện Thoại:</label>
                            <Field name="numberphone" />
                            <ErrorMessage name="numberphone" component="span" />
                        </div>

                        <div>
                            <label>Tên Đăng Nhập:</label>
                            <Field name="username" />
                            <ErrorMessage name="username" component="span" />
                        </div>

                        <div>
                            <label>Email:</label>
                            <Field name="email" />
                            <ErrorMessage name="email" component="span" />
                        </div>

                        <div>
                            <label>Lương:</label>
                            <Field name="salary" type="number" />
                            <ErrorMessage name="salary" component="span" />
                        </div>

                        <div>
                            <label>Giới Tính:</label>
                            <div>
                                <label>
                                    <Field type="radio" name="gender" value="true" />
                                    Nam
                                </label>
                                <label>
                                    <Field type="radio" name="gender" value="false" />
                                    Nữ
                                </label>
                                <ErrorMessage name="gender" component="span" />
                            </div>
                        </div>

                        <div>
                            <label>Ảnh:</label>
                            <input type="file" onChange={(e) => handleImageChange(e, setFieldValue)} />
                            {url && <img src={url} alt="Uploaded" width="100" />}
                        </div>

                        <div>
                            <label>Quyền:</label>
                            <Field name="roleId" as="select">
                                <option value="1">Nhân viên</option>
                                <option value="2">Admin</option>
                            </Field>
                            <ErrorMessage name="roleId" component="span" />
                        </div>

                        <div>
                            <button type="submit">Cập nhật</button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
    );
}
