// Component/AddEmployeeForm.js
import React, { useState } from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";
import { useNavigate } from "react-router-dom";
import { saveEmployeeToAPI } from "../Service/UserService";
import { storage } from "../FirebaseConfig/firebase"; // Nhập storage
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";

export default function AddEmployeeForm() {
    const [url, setUrl] = useState(""); // URL của ảnh sau khi upload
    const navigate = useNavigate();
    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6Imh1dUAxMjM0NSIsInN1YiI6Imh1dUAxMjM0NSIsImV4cCI6MTczMzE0MDI0MH0.ECz7k2EOiiST8zXMRUxhpgxjQ9w-yB3dPMLQNSYNp78'; // Thay thế bằng token của bạn

    const initialValues = {
        username: "",
        fullName: "",
        address: "",
        numberphone: "",
        gender: true, // True là Nam, False là Nữ
        birthday: "",
        salary: 0,
        roleId: "", // Role chưa chọn
        password: "",
        email: "",
        url:"", // Thêm trường imgUrl
    };

    const validateEmployee = {
        fullName: Yup.string().required("Tên đầy đủ là bắt buộc."),
        address: Yup.string().required("Địa chỉ là bắt buộc."),
        birthday: Yup.date().required("Ngày sinh là bắt buộc."),
        numberphone: Yup.string().required("Số điện thoại là bắt buộc."),
        username: Yup.string().required("Tên đăng nhập là bắt buộc."),
        password: Yup.string().required("Mật khẩu là bắt buộc."),
        email: Yup.string().email("Email không hợp lệ").required("Email là bắt buộc."),
        salary: Yup.number().min(0, "Lương phải lớn hơn 0").required("Lương là bắt buộc."),
        roleId: Yup.string().required("Quyền là bắt buộc."),
    };

    const handleUpload = async (file, setFieldValue) => {
        const storageRef = ref(storage, `images/${file.name}`);

        console.log("Uploading file:", file);
        console.log("Firebase storage reference:", storageRef);

        try {
            const snapshot = await uploadBytes(storageRef, file);
            console.log("File uploaded successfully:", snapshot);
            const downloadURL = await getDownloadURL(snapshot.ref);
            console.log("Download URL:", downloadURL);

            setFieldValue("imgUrl", downloadURL);
            setUrl(downloadURL);
        } catch (error) {
            console.error("Upload failed:", error);
        }
    };


    const handleImageChange = (e, setFieldValue) => {
        const file = e.target.files[0];
        console.log("Selected file:", file); // Thêm log để xem file đã chọn
        if (file) {
            handleUpload(file, setFieldValue); // Gọi hàm upload
        }
    };

    const handleBack = () => {
        navigate("/users"); // Điều hướng trở về trang danh sách người dùng
    };

    const handleSubmitEmployee = async (values) => {
        console.log("Dữ liệu trước khi submit:", values);  // Kiểm tra giá trị imgUrl ở đây
        try {
            await saveEmployeeToAPI(values, token);
            navigate("/users");
        } catch (error) {
            console.error("Có lỗi xảy ra khi lưu nhân viên.", error);
        }
    };


    return (
        <div className="update-employee-container">
            <Formik
                initialValues={initialValues}
                onSubmit={handleSubmitEmployee}
                validationSchema={Yup.object(validateEmployee)}
            >
                {({ setFieldValue }) => (
                    <Form>
                        <h1>Thêm mới Nhân Viên</h1>

                        <div>
                            <label>Tên Đầy Đủ:</label>
                            <Field name="fullName" />
                            <ErrorMessage name="fullName" component="span" />
                        </div>

                        <div>
                            <label>Địa Chỉ:</label>
                            <Field name="address" />
                            <ErrorMessage name="address" component="span" />
                        </div>

                        <div>
                            <label>Ngày Sinh:</label>
                            <Field name="birthday" type="date" />
                            <ErrorMessage name="birthday" component="span" />
                        </div>

                        <div>
                            <label>Số Điện Thoại:</label>
                            <Field name="numberphone" />
                            <ErrorMessage name="numberphone" component="span" />
                        </div>

                        <div>
                            <label>Tên Đăng Nhập:</label>
                            <Field name="username" />
                            <ErrorMessage name="username" component="span" />
                        </div>

                        <div>
                            <label>Mật Khẩu:</label>
                            <Field name="password" type="password" />
                            <ErrorMessage name="password" component="span" />
                        </div>

                        <div>
                            <label>Email:</label>
                            <Field name="email" />
                            <ErrorMessage name="email" component="span" />
                        </div>

                        <div>
                            <label>Lương:</label>
                            <Field name="salary" type="number" />
                            <ErrorMessage name="salary" component="span" />
                        </div>

                        <div>
                            <label>Giới Tính:</label>
                            <Field name="gender" as="select">
                                <option value="true">Nam</option>
                                <option value="false">Nữ</option>
                            </Field>
                        </div>

                        <div>
                            <label>Ảnh:</label>
                            <input type="file" onChange={(e) => handleImageChange(e, setFieldValue)} />
                            {url && <img src={url} alt="Uploaded" width="100" />}
                        </div>

                        <div>
                            <label>Quyền:</label>
                            <Field name="roleId" as="select">
                                <option value="">Chọn quyền</option>
                                <option value="1">Admin</option>
                                <option value="2">User</option>
                            </Field>
                            <ErrorMessage name="roleId" component="span" />
                        </div>

                        <div>
                            <button type="submit">Thêm mới</button>
                        </div>

                        <div>
                            <button type="button" onClick={handleBack}>Trở về</button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
    );
}
