import React, { useEffect, useState } from "react";
import { getUsers, searchUsers } from "../Service/UserService";

export default function UserList() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [page, setPage] = useState(0);
    const [totalPages, setTotalPages] = useState(0);

    // Các biến state cho tìm kiếm
    const [userName, setUserName] = useState("");
    const [fullName, setFullName] = useState("");
    const [numberPhone, setNumberPhone] = useState("");
    const [isSearching, setIsSearching] = useState(false);

    // Biến state tạm thời cho giá trị input
    const [searchUserName, setSearchUserName] = useState("");
    const [searchFullName, setSearchFullName] = useState("");
    const [searchNumberPhone, setSearchNumberPhone] = useState("");

    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6ImNoYW5odHYiLCJzdWIiOiJjaGFuaHR2IiwiZXhwIjoxNzMzNDU0MTYwfQ.kj4qPTp9b9WLx4NeIrEGuwWakr7QR1Td7Zqu3LkIGR4';

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const data = isSearching
                    ? await searchUsers(userName, fullName, numberPhone, token, page)
                    : await getUsers(token, page);
                setUsers(data.content);
                setTotalPages(data.totalPages);
            } catch (error) {
                setError("Có lỗi xảy ra khi lấy danh sách người dùng.");
            }
            setLoading(false);
            setIsSearching(false); // Đặt lại isSearching về false sau khi tìm kiếm
        };

        fetchData();
    }, [page, isSearching]); // Chỉ phụ thuộc vào page và isSearching

    // Hàm tìm kiếm
    const handleSearch = () => {
        setPage(0); // Đặt lại trang về trang đầu tiên khi tìm kiếm mới
        setUserName(searchUserName);
        setFullName(searchFullName);
        setNumberPhone(searchNumberPhone);
        setIsSearching(true); // Đặt chế độ tìm kiếm
    };

    const handleNextPage = () => {
        if (page < totalPages - 1) {
            setPage((prevPage) => prevPage + 1);
        }
    };

    const handlePrevPage = () => {
        if (page > 0) {
            setPage((prevPage) => prevPage - 1);
        }
    };

    if (loading) return <p>Đang tải...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Danh Sách Người Dùng</h2>

            {/* Form tìm kiếm */}
            <div>
                <input
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={searchUserName}
                    onChange={(e) => setSearchUserName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Họ và tên"
                    value={searchFullName}
                    onChange={(e) => setSearchFullName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Số điện thoại"
                    value={searchNumberPhone}
                    onChange={(e) => setSearchNumberPhone(e.target.value)}
                />
                <button onClick={handleSearch}>Tìm kiếm</button>
            </div>

            {users.length > 0 ? (
                <>
                    <table>
                        <thead>
                        <tr>
                            <th>STT</th>
                            <th>Tên tài khoản</th>
                            <th>Họ và tên</th>
                            <th>Địa chỉ</th>
                            <th>Số điện thoại</th>
                            <th>Giới tính</th>
                            <th>Ngày sinh</th>
                            <th>Lương</th>
                            <th>Vị trí</th>
                        </tr>
                        </thead>
                        <tbody>
                        {users.map((user, index) => (
                            <tr key={user.userId}>
                                <td>{index + 1 + page * 5}</td>
                                <td>{user.username}</td>
                                <td>{user.fullName}</td>
                                <td>{user.address}</td>
                                <td>{user.numberphone}</td>
                                <td>{user.gender ? "Nam" : "Nữ"}</td>
                                <td>{new Date(user.birthday).toLocaleDateString()}</td>
                                <td>{user.salary}</td>
                                <td>{user.role.roleName}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>

                    {/* Phân trang */}
                    <div>
                        <button onClick={handlePrevPage} disabled={page === 0}>
                            Trang trước
                        </button>
                        <span> Trang {page + 1} / {totalPages} </span>
                        <button onClick={handleNextPage} disabled={page === totalPages - 1}>
                            Trang sau
                        </button>
                    </div>
                </>
            ) : (
                <p>Không có người dùng nào.</p>
            )}
        </div>
    );
}
