import React, { useEffect, useState } from "react";
import { getUsers } from "../Service/UserService";
import Modal from "bootstrap/js/src/modal";

export default function UserList() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null); // Thêm state để lưu người dùng đã chọn
    const [isModalOpen, setIsModalOpen] = useState(false); // State để kiểm soát modal


    // Token được lấy từ Postman
    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6Imh1dUAxMjM0NSIsInN1YiI6Imh1dUAxMjM0NSIsImV4cCI6MTczMzE0MjkyM30.Z1XjF24hSJjRZBLywf7hJ1g-QZa34ve1B4020rZsQcg';

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getUsers(token);
                // Lọc chỉ những người dùng có lương > 0
                const filteredUsers = data.filter(user => user.salary > 0);
                setUsers(filteredUsers);
            } catch (error) {
                setError("Có lỗi xảy ra khi lấy danh sách người dùng.");
            }
            setLoading(false);
        };

        fetchData();
    }, []);

    if (loading) return <p>Đang tải...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Danh Sách Người Dùng</h2>
            {users.length > 0 ? ( // Kiểm tra xem có người dùng nào không
                <table>
                    <thead>
                    <tr>
                        <th>STT</th>
                        <th>Tên tài khoản</th>
                        <th>Họ và tên</th>
                        <th>Địa chỉ</th>
                        <th>Số điện thoại</th>
                        <th>Giới tính</th>
                        <th>Ngày sinh</th>
                        <th>Lương</th>
                        <th>Vị trí</th>
                    </tr>
                    </thead>
                    <tbody>
                    {users.map((user, index) => (
                        <tr key={user.userId}>
                            <td>{index + 1}</td>
                            <td>{user.username}</td>
                            <td>{user.fullName}</td>
                            <td>{user.address}</td>
                            <td>{user.numberphone}</td>
                            <td>{user.gender ? "Nam" : "Nữ"}</td>
                            <td>{new Date(user.birthday).toLocaleDateString()}</td>
                            <td>{user.salary}</td>
                            <td>{user.role.roleName}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            ) : (
                <p>Không có người dùng nào.</p> // Thông báo nếu không có người dùng nào
            )}
            {/* Modal hiển thị thông tin chi tiết */}
            {selectedUser && (
                <Modal isOpen={isModalOpen} onRequestClose={closeModal}>
                    <h2>Thông Tin Chi Tiết</h2>
                    <img src={selectedUser.imgUrl} alt="User" style={{ width: '100px' }} /> {/* Hiển thị hình ảnh */}
                    <p><strong>Tên:</strong> {selectedUser.fullName}</p>
                    <p><strong>Địa chỉ:</strong> {selectedUser.address}</p>
                    <p><strong>Ngày sinh:</strong> {new Date(selectedUser.birthday).toLocaleDateString()}</p>
                    <p><strong>Số điện thoại:</strong> {selectedUser.numberphone}</p>
                    <p><strong>Tên đăng nhập:</strong> {selectedUser.username}</p>
                    <p><strong>Email:</strong> {selectedUser.email}</p>
                    <p><strong>Giới tính:</strong> {selectedUser.gender ? "Nam" : "Nữ"}</p>
                    <p><strong>Lương:</strong> {selectedUser.salary}</p>
                    <p><strong>Vị trí:</strong> {selectedUser.role.roleName}</p>
                    <button onClick={closeModal}>Đóng</button> {/* Nút đóng modal */}
                </Modal>
            )}
        </div>
    );
}
