import React, { useEffect, useState } from "react";
import { getUsers, searchUsers } from "../Service/UserService";
import {Link} from "react-router-dom";

export default function UserList() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [page, setPage] = useState(0);
    const [totalPages, setTotalPages] = useState(0);

    // Các biến state cho tìm kiếm
    const [userName, setUserName] = useState("");
    const [fullName, setFullName] = useState("");
    const [numberPhone, setNumberPhone] = useState("");
    const [isSearching, setIsSearching] = useState(false); // Trạng thái tìm kiếm

    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6Im5ndXllbmRpbmhoYXVhY2U4Iiwic3ViIjoibmd1eWVuZGluaGhhdWFjZTgiLCJleHAiOjE3MzM2MzQ5OTR9.etthqmLdwF92-_maSQJYO05NrvkA_k9mJtGmksZzW0I';

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const data = isSearching
                    ? await searchUsers(userName, fullName, numberPhone, token, page)
                    : await getUsers(token, page);
                setUsers(data.content);
                setTotalPages(data.totalPages);
            } catch (error) {
                setError("Có lỗi xảy ra khi lấy danh sách người dùng.");
            }
            setLoading(false);
        };

        fetchData();
    }, [page,isSearching]); // Chỉ theo dõi `page`

    // Hàm tìm kiếm
    const handleSearch = () => {
        setIsSearching(true); // Đặt chế độ tìm kiếm
        setPage(0); // Đặt lại trang về trang đầu tiên khi tìm kiếm mới
        // Gọi lại fetchData với các tham số tìm kiếm
        const fetchData = async () => {
            setLoading(true);
            try {
                const data = await searchUsers(userName, fullName, numberPhone, token, 0); // Sử dụng page 0 cho tìm kiếm
                setUsers(data.content);
                setTotalPages(data.totalPages);
            } catch (error) {
                setError("Có lỗi xảy ra khi tìm kiếm người dùng.");
            }
            setLoading(false);
        };
        fetchData();
    };
    // Hàm resetSearch để đưa về trạng thái ban đầu
    const resetSearch = () => {
        setUserName("");
        setFullName("");
        setNumberPhone("");
        setIsSearching(false);
        setPage(0);
    };
    const handleNextPage = () => {
        if (page < totalPages - 1) {
            setPage((prevPage) => prevPage + 1);
        }
    };

    const handlePrevPage = () => {
        if (page > 0) {
            setPage((prevPage) => prevPage - 1);
        }
    };

    if (loading) return <p>Đang tải...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Danh Sách Người Dùng</h2>

            {/* Form tìm kiếm */}
            <div>
                <input
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Họ và tên"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Số điện thoại"
                    value={numberPhone}
                    onChange={(e) => setNumberPhone(e.target.value)}
                />
                <button onClick={handleSearch}>Tìm kiếm</button>
                <button onClick={resetSearch}>Hiển thị tất cả</button> {/* Nút reset tìm kiếm */}

            </div>

            {users.length > 0 ? (
                <>
                    <table>
                        <thead>
                        <tr>
                            <th>STT</th>
                            <th>Tên tài khoản</th>
                            <th>Họ và tên</th>
                            <th>Địa chỉ</th>
                            <th>Số điện thoại</th>
                            <th>Giới tính</th>
                            <th>Ngày sinh</th>
                            <th>Lương</th>
                            <th>Vị trí</th>
                            <th>Tác Vụ</th>
                        </tr>
                        </thead>
                        <tbody>
                        {users.map((user, index) => (
                            <tr key={user.userId}>
                                <td>{index + 1 + page * 10}</td>
                                <td>{user.username}</td>
                                <td>{user.fullName}</td>
                                <td>{user.address}</td>
                                <td>{user.numberphone}</td>
                                <td>{user.gender ? "Nam" : "Nữ"}</td>
                                <td>{new Date(user.birthday).toLocaleDateString()}</td>
                                <td>{user.salary}</td>
                                <td>{user.role.roleName}</td>
                                <td>
                                    <Link to={`/users/update/${user.userId}`}>Cập nhật</Link> {/* Thêm liên kết Cập nhật */}
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>

                    {/* Phân trang */}
                    <div>
                        <button onClick={handlePrevPage} disabled={page === 0}>
                            Trang trước
                        </button>
                        <span> Trang {page + 1} / {totalPages} </span>
                        <button onClick={handleNextPage} disabled={page === totalPages - 1}>
                            Trang sau
                        </button>
                    </div>
                </>
            ) : (
                <p>Không có người dùng nào.</p>
            )}
        </div>
    );
}
