import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import { ref, uploadBytesResumable, getDownloadURL } from 'firebase/storage';
import { storage } from '../FirebaseConfig/firebase'; // Firebase config
import * as Yup from "yup";
import { toast } from "react-toastify";
import { ErrorMessage, Field, Form, Formik } from "formik";
import axios from 'axios'; // Import axios để thực hiện POST request

function CreateEmployee() {
    const navigate = useNavigate();
    const [image, setImage] = useState(null);
    const [url, setUrl] = useState("");

    const initialValues = {
        fullName: "",
        address: "",
        birthday: "",
        numberphone: "",
        username: "",
        password: "",
        email: "",
        gender: false,
        salary: "",
        isActive: true,
        roleId: "",
    };

    const validateEmployee = {
        fullName: Yup.string().required("Vui lòng nhập tên đầy đủ"),
        address: Yup.string().required("Vui lòng nhập địa chỉ"),
        birthday: Yup.date().required("Vui lòng chọn ngày sinh"),
        numberphone: Yup.string()
            .required("Vui lòng nhập số điện thoại")
            .matches(/^(03|05|07|08|09)\d{8}$/, "Số điện thoại không đúng định dạng"),
        username: Yup.string()
            .required("Vui lòng nhập tên đăng nhập")
            .min(6, "Tên đăng nhập phải lớn hơn 6 ký tự")
            .matches(/^[^0-9].*/, "Tên đăng nhập không được bắt đầu bằng số."),
        password: Yup.string()
            .required("Vui lòng nhập mật khẩu")
            .min(8, "Mật khẩu phải có ít nhất 8 ký tự"),
        email: Yup.string()
            .required("Vui lòng nhập email")
            .email("Email không hợp lệ")
            .max(50, "Email không được vượt quá 50 ký tự"),
        salary: Yup.number().required("Vui lòng nhập lương").min(0, "Lương phải là số dương"),
        roleId: Yup.string().required("Vui lòng chọn quyền"),
    };

    const handleImageChange = (e) => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    };

    const handleUpload = async (setFieldValue) => {
        if (image) {
            const storageRef = ref(storage, `images/${image.name}`);
            const uploadTask = uploadBytesResumable(storageRef, image);

            uploadTask.on(
                "state_changed",
                (snapshot) => {},
                (error) => {
                    console.error("Lỗi khi upload:", error);
                },
                async () => {
                    const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
                    setUrl(downloadURL);
                    setFieldValue("url", downloadURL); // Đặt URL ảnh vào form
                    toast.success("Upload ảnh thành công!");
                }
            );
        }
    };

    const saveEmployee = async (values) => {
        // Lấy token từ localStorage
        const token = localStorage.getItem('jwtToken');

        try {
            const response = await axios.post("http://localhost:8080/api/users", values, {
                headers: {
                    Authorization: `Bearer ${token}`, // Gửi token trong header
                },
            });
            if (response.status === 201) {
                toast.success("Thêm mới nhân viên thành công!");
                navigate("/users");
            }
        } catch (error) {
            toast.error("Lỗi khi thêm mới nhân viên: " + (error.response?.data?.message || error.message));
        }
    };

    const handleBack = () => {
        navigate('/users');
    };

    return (
        <div className="update-employee-container">
            <Formik
                initialValues={initialValues}
                onSubmit={saveEmployee}
                validationSchema={Yup.object(validateEmployee)}
            >
                {({ setFieldValue }) => (
                    <Form>
                        <h1>Thêm mới Nhân Viên</h1>

                        <div>
                            <label>Tên Đầy Đủ:</label>
                            <Field name="fullName" />
                            <ErrorMessage name="fullName" component="span" />
                        </div>

                        <div>
                            <label>Địa Chỉ:</label>
                            <Field name="address" />
                            <ErrorMessage name="address" component="span" />
                        </div>

                        <div>
                            <label>Ngày Sinh:</label>
                            <Field name="birthday" type="date" />
                            <ErrorMessage name="birthday" component="span" />
                        </div>

                        <div>
                            <label>Số Điện Thoại:</label>
                            <Field name="numberphone" />
                            <ErrorMessage name="numberphone" component="span" />
                        </div>

                        <div>
                            <label>Tên Đăng Nhập:</label>
                            <Field name="username" />
                            <ErrorMessage name="username" component="span" />
                        </div>

                        <div>
                            <label>Mật Khẩu:</label>
                            <Field name="password" type="password" />
                            <ErrorMessage name="password" component="span" />
                        </div>

                        <div>
                            <label>Email:</label>
                            <Field name="email" />
                            <ErrorMessage name="email" component="span" />
                        </div>

                        <div>
                            <label>Lương:</label>
                            <Field name="salary" type="number" />
                            <ErrorMessage name="salary" component="span" />
                        </div>

                        <div>
                            <label>Giới Tính:</label>
                            <Field name="gender" as="select">
                                <option value="true">Nam</option>
                                <option value="false">Nữ</option>
                            </Field>
                        </div>

                        <div>
                            <label>Ảnh:</label>
                            <input type="file" onChange={handleImageChange} />
                            <button type="button" onClick={() => handleUpload(setFieldValue)}>
                                Upload Ảnh
                            </button>
                            {url && <img src={url} alt="Uploaded" width="100" />}
                        </div>

                        <div>
                            <label>Quyền:</label>
                            <Field name="roleId" as="select">
                                <option value="">Chọn quyền</option>
                                <option value="1">Admin</option>
                                <option value="2">User</option>
                            </Field>
                            <ErrorMessage name="roleId" component="span" />
                        </div>

                        <div>
                            <button type="submit">Thêm mới</button>
                        </div>

                        <div>
                            <button type="button" onClick={handleBack}>Trở về</button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
    );
}

export default CreateEmployee;
