import React, { useState } from "react";
import { saveEmployee } from "../Service/UserService";
import { useNavigate } from "react-router-dom";

export default function AddEmployeeForm() {
    const [employeeData, setEmployeeData] = useState({
        username: "",
        fullName: "",
        address: "",
        numberphone: "",
        gender: true,  // True là Nam, False là Nữ
        birthday: "",
        salary: 0,
        roleId: 1,  // Ví dụ roleId là 1
    });

    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6Imh1dUAxMjM0NSIsInN1YiI6Imh1dUAxMjM0NSIsImV4cCI6MTczMzExMTEwM30.jHmYTTNI48aVeWiHxkFokOmhIef2pzm5uEUHTEvjam8';

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await saveEmployee(employeeData, token);
            navigate("/users"); // Điều hướng sau khi thêm mới thành công
        } catch (error) {
            setError("Có lỗi xảy ra khi thêm mới nhân viên.");
        }
    };

    return (
        <div>
            <h2>Thêm Mới Nhân Viên</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                {/* Các input để nhập thông tin nhân viên */}
                <input
                    type="text"
                    placeholder="Tên tài khoản"
                    value={employeeData.username}
                    onChange={(e) => setEmployeeData({ ...employeeData, username: e.target.value })}
                    required
                />
                {/* Tương tự cho các trường khác: fullName, address, numberphone, birthday... */}
                <button type="submit">Lưu</button>
            </form>
        </div>
    );
}
