import React, { useEffect, useState } from "react";
import { getUsers } from "../Service/UserService";

export default function UserList() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [page, setPage] = useState(0); // Số trang hiện tại
    const [totalPages, setTotalPages] = useState(0); // Tổng số trang
    const [username, setUsername] = useState(""); // Trạng thái cho tên đăng nhập
    const [fullName, setFullName] = useState(""); // Trạng thái cho họ và tên
    const [numberPhone, setNumberPhone] = useState(""); // Trạng thái cho số điện thoại

    // Token được lấy từ Postman
    const token = 'eyJhbGciOiJIUzI1NiJ9.eyJ1c2VybmFtZSI6ImNoYW5odHYiLCJzdWIiOiJjaGFuaHR2IiwiZXhwIjoxNzMzMjg4Njg3fQ.rYZ75Kilq2yAFsfgmGgZ4EToO7N8fC6rxoMZCGCiX_k';

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getUsers(token, page, username, fullName, numberPhone); // Gọi API với tham số phân trang và tìm kiếm

                setUsers(data.content);
                setTotalPages(data.totalPages); // Lưu tổng số trang từ backend
            } catch (error) {
                setError("Có lỗi xảy ra khi lấy danh sách người dùng.");
            }
            setLoading(false);
        };

        fetchData();
    }, [page, username, fullName, numberPhone]); // Cập nhật dữ liệu khi trang hoặc tham số tìm kiếm thay đổi

    const handleNextPage = () => {
        if (page < totalPages - 1) {
            setPage(prevPage => prevPage + 1); // Chuyển đến trang tiếp theo
        }
    };

    const handlePrevPage = () => {
        if (page > 0) {
            setPage(prevPage => prevPage - 1); // Quay lại trang trước
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        setPage(0); // Reset về trang 0 khi tìm kiếm
    };

    if (loading) return <p>Đang tải...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Danh Sách Người Dùng</h2>

            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Họ và tên"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Số điện thoại"
                    value={numberPhone}
                    onChange={(e) => setNumberPhone(e.target.value)}
                />
                <button type="submit">Tìm kiếm</button>
            </form>

            {users.length > 0 ? (
                <>
                    <table>
                        <thead>
                        <tr>
                            <th>STT</th>
                            <th>Tên tài khoản</th>
                            <th>Họ và tên</th>
                            <th>Địa chỉ</th>
                            <th>Số điện thoại</th>
                            <th>Giới tính</th>
                            <th>Ngày sinh</th>
                            <th>Lương</th>
                            <th>Vị trí</th>
                        </tr>
                        </thead>
                        <tbody>
                        {users.map((user, index) => (
                            <tr key={user.userId}>
                                <td>{index + 1 + (page * 2)}</td>
                                <td>{user.username}</td>
                                <td>{user.fullName}</td>
                                <td>{user.address}</td>
                                <td>{user.numberphone}</td>
                                <td>{user.gender ? "Nam" : "Nữ"}</td>
                                <td>{new Date(user.birthday).toLocaleDateString()}</td>
                                <td>{user.salary}</td>
                                <td>{user.role.roleName}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>

                    {/* Phân trang */}
                    <div>
                        <button onClick={handlePrevPage} disabled={page === 0}>
                            Trang trước
                        </button>
                        <span> Trang {page + 1} / {totalPages} </span>
                        <button onClick={handleNextPage} disabled={page === totalPages - 1}>
                            Trang sau
                        </button>
                    </div>
                </>
            ) : (
                <p>Không có người dùng nào.</p>
            )}
        </div>
    );
}
